import React from 'react';
import MyButton from "../../UI/button/MyButton";

const textItem=document.querySelector('.text_item');
const textBtn=document.querySelector('.text_btn');
const state={
    texts:[]
};




async function AddText() {


    return await fetch('https://baconipsum.com/api/?type=all-meat&sentences=5&start-with-lorem=1', {
        paras: {
            "Content-type": "aplication/json; charset=UTF-8"
        }
    })
        .then(res => res.json())
        .then((text) => console.log('paras', text))
        .then((texts)=>state.texts=state.texts.concat(texts))

}

export default AddText;
