    async function getText() {
        text = await fetch('https://baconipsum.com/api/?type=all-meat&sentences=5&start-with-lorem=1')
            .then(res => res.json())
            .then(data => text = data[0])
            .then(() => setValue(text));
        // .then(() => console.log(text[0]));
        return text
    }
