        if (txt[0] === typeTxt[0]){
            for (let i=0; i<txt.length; i++){
                error.map(value => value)
            }
        }

    };

    console.log(txt);
    console.log(typeTxt);
    console.log(error);



    return (
        <div>
            <input onChange={handleChange}   value={key} type="text" />
            <div className="random_txt">{value}</div>

            <button className="btn_txt" onClick={getText}>
                Try again!
            </button>

        </div>

    );
};

export default ViewText;
