    let text=[];

    const [value, setValue] = useState('');
    const [addText, setAddText]=useState('');
    const [counter,setCounter]=useState('100');

    async function getText() {
        text = await fetch('https://baconipsum.com/api/?type=all-meat&sentences=5&start-with-lorem=1')
            .then(res => res.json())
            .then(data => text = data[0])
            .then(() => setValue(text));
            // .then(() => console.log(text[0]));

        return text
    }


    const txt = Object.values(value).map(([value]) => ({value}));

    console.log(txt);

    function compareLetters() {
        // for (let i=0; i<txt.value.length; i++)
        if (txt[0]=text[0]){
            setCounter(counter -100/450)

        }

        return counter;
    }



    return (
        <div>
            <div>
                <span>Напечатай Меня!</span>
            </div>
            <input className="add_text" type='text' value={addText} onChange={event => setAddText(event.target.addText)}/>
            <div>{value}</div>
            {/*<MyInput*/}
            {/*    type='text'*/}
            {/*    value={value}*/}
            {/*    // onChange={event => setValue(event.target.value)}*/}
            {/*/>*/}
            <MyButton onClick={getText}>
                Let`s start!
