import React, {useState} from 'react';
import InputText from "./inputText";
import MyButton from "../../UI/button/MyButton";
import {useEffect} from "react";

const ViewText = () => {

    const [key, setKey] = useState("");




    let text = [];
    const [value, setValue] = useState('');



    async function getText() {
        text = await fetch('https://baconipsum.com/api/?type=all-meat&sentences=5&start-with-lorem=1')
            .then(res => res.json())
            .then(data => text = data[0])
            .then(() => setValue(text));
        // .then(() => console.log(text[0]));
        return text
    }

    const txt = Object.values(value).map(([value]) => ({value}));

    useEffect(() => {
        getText();
    }, []);

    function handleChange(e){
        setKey(e.target.value);
    }

    return (
        <div>



            <div className="random_txt">{value}</div>

            <button className="btn_txt" onClick={getText}>
                Try again!
            </button>
            <input onChange={handleChange}   value={key} type="text" />



        </div>

    );
};

export default ViewText;
