        setValue(e.target.value);
    }
    function handleBackSpace(e){
        if(e.keyCode === 8){
            //Do something.
        }
    }
    return (
        <div>
            <input onChange={handleChange} onKeyDown={handleBackSpace} value={value} type="text" />
        </div>
    )
};
