import React from 'react';
import classes from "./keyboard.module.css"


const Keyboard = () => {
    const input = document.querySelector("input");
    const letters = Array.from(document.querySelectorAll("[data-letters]"));
    const specs = Array.from(document.querySelectorAll("[data-spec]"));
    const textExample=document.querySelector('#textExample');
    const speed=document.querySelector("#symbolsPerMinute");
    const accuracy=document.querySelector("#errorPercent");


    const text = `Изучая факторы обеспечившие непрерывность и устойчивость китайской цивилизации, авторы четырехтомника говорят о ее колоссальной
центростремительной силе, беспрестанном самосовершенствовании и моральном стержне, связанном в первую очередь с культом предков. Эта непрерывная,
единая цивилизация исходила из своей собственной логики эволюции и развития. В отличие от большинства других древних цивилизаций, давно сошедших
с исторической сцены, китайская занимала значительное пространство, что создавало благоприятные условия для ассимиляции и интеграции иных народов.
Главными условиями этого были воспитание и ненасильственное распространение китайской культуры.
Сущность китайской цивилизации авторы выражают термином «единство многообразия» (доюань итигэ), называют ее многокомпонентной и полиэтничной
(доюань вэньмин). Факторами, обеспечившими стабильность ее развития, называют восприимчивость к элементам пришедших извне культур, взаимодействие
элитарного и простонародного, а также «новаторство через возвращение к древности». Большое внимание в четырехтомнике уделяется вкладу китайской
цивилизации в мировую культуру, ее самобытным и разнообразным достижениям в науке и искусстве.`;

    const part = createPart(text);

    init();

    function init() {
        addEventListener('keydown', onkeydown);
        addEventListener('keyup', onkeyup);
        viewUpdate();

    }

    function onkeydown(event) {
        event.preventDefault();

        const letter = letters.find((x) => x.dataset.letters.includes(event.key));

        if (letter) {
            letter.classList.add("pressed");
            pressedWords(event.key);
            return;
        }

        let key = event.key.toLowerCase();

        if (key === " ") {
            key = "space";
            pressedWords( " ")
        }

        if (key ==="enter"){
            pressedWords("\n")
        }

        const ownSpecs = specs.filter((x) => x.dataset.spec === key);
        if (ownSpecs.length) {
            ownSpecs.forEach((spec) => spec.classList.add("pressed"));
            return;
        }
    }

    function onkeyup(event) {
        event.preventDefault();

        const letter = letters.find((x) => x.dataset.letters.includes(event.key));

        if (letter) {
            letter.classList.remove("pressed");
            return;
        }

        let key = event.key.toLowerCase();

        if (key === " ") {
            key = "space";
        }


        const ownSpecs = specs.filter((x) => x.dataset.spec === key);
        if (ownSpecs.length) {
            ownSpecs.forEach((spec) => spec.classList.remove("pressed"));
            return;
        }

    }

    function createPart() {
        const part = {
            text,
            strings: [],
            maxStringLength: 70,
            maxShowStrings: 3,
            currentStringIndex: 0,
            currentPrintedIndex: 0,
            sumSymbols:0,
            errors: [],
            errorCounter:0,
            startingTimer: 0,
            beginningTime:false,
            timeCounter:0,
            statFlag: false,

        };

        part.text = part.text.replace(/\n/g, '\n ');
        const words = part.text.split(" ");

        let string = [];
        for (const word of words) {
            const newStringLength = [...string, word].join(" ").length + !word.includes("\n");

            if (newStringLength > part.maxStringLength) {
                part.strings.push(string.join(' ') + ' ');
                string = []
            }

            string.push(word);

            if (word.includes('\n')) {
                part.strings.push(string.join(' '));
                string = []
            }
        }
        if (string.length) {
            part.strings.push(string.join(' '))

        }
        return part;
    }

    function pressedWords(letter) {
        part.beginningTime=true;
        if (!part.statFlag){
            part.statFlag=true;
            part.startingTimer=Date.now();
        }
        const string = part.strings[part.currentStringIndex];
        const printedLetter = string[part.currentPrintedIndex];

        if (letter === printedLetter) {
            part.currentPrintedIndex++;
            if (string.length <= part.currentPrintedIndex) {
                part.currentPrintedIndex = 0;
                part.currentStringIndex++;

                part.statFlag=false;
                part.timeCounter=Date.now()-part.startingTimer;

            }
        } else if (!part.errors.includes(printedLetter)) {
            part.errors.push(printedLetter);
            part.errorCounter++;
        }

        part.sumSymbols++;
        viewUpdate();

    }

    function viewUpdate() {
        const string = part.strings[part.currentStringIndex];
        const showedStrings = part.strings.slice(
            part.currentStringIndex,
            part.currentStringIndex + part.maxShowStrings
        );
        const div=document.createElement('div');

        const firstString=document.createElement('div');
        firstString.classList.add('line');
        div.append(firstString);

        const printedString=document.createElement('span');
        printedString.classList.add('done');
        printedString.textContent=string.slice(0, part.currentPrintedIndex);

        firstString.append(
            printedString,
            ...string
                .slice(part.currentPrintedIndex)
                .split("")
                .map((letter) => {
                    if (letter===" "){
                        return "•"
                    }

                    if (part.errors.includes(letter)){
                        const errorSpan=document.createElement('span');
                        errorSpan.classList.add('hint');
                        errorSpan.textContent=letter;
                        return errorSpan;
                    }
                    return letter;
                })
        );

        for (let i = 1; i < showedStrings.length; i++) {
            const nextString = document.createElement('div');
            nextString.classList.add('line');
            div.append(nextString);

            nextString.append(
                ...showedStrings[i]
                    .split("").map((letter) => {
                        if (letter === " ") {
                            return "•"
                        }

                        if (part.errors.includes(letter)){
                            const errorSpan=document.createElement('span');
                            errorSpan.classList.add('hint');
                            errorSpan.textContent=letter;
                            return errorSpan;
                        }
                        return letter;
                    })
            );
        }

        textExample.innerHTML="";
        textExample.append(div);

        input.value=string.slice(0, part.currentPrintedIndex);

        if (!part.statFlag && part.beginningTime) {
            speed.textContent = Math.round(part.sumSymbols / (part.timeCounter / 1000 / 60));
            accuracy.textContent = Math.floor((10000 * part.errorCounter) / part.sumSymbols) / 100 + "%";
        }
    };
    return (
        <div>
        <div className="text" id="textExample">
            <div className="line line-1">
                <div>
                    <span className="done"> На переднем плане, прямо перед</span>
                    <span className="hint">н</span>ами, расположен был дворик, где стоял
                </div>
                <div className="line">
                    наполовину вычищенный автомобиль. Шофер Остин был на этот раз
                </div>
                <div className="line">
                    уволен окончательно и бесповоротно. Он раскинулся на земле,
                </div>
            </div>
        </div>

            <div className="keyboard">
                <div className="line">
                    <div id="letter_ё" data-letters="Ёё">Ё</div>
                    <div id="letter_1" data-letters="1">1</div>
                    <div id="letter_2" data-letters="2">2</div>
                    <div id="letter_3" data-letters="3">3</div>
                    <div id="letter_4" data-letters="4">4</div>
                    <div id="letter_5" data-letters="5">5</div>
                    <div id="letter_6" data-letters="6">6</div>
                    <div id="letter_7" data-letters="7">7</div>
                    <div id="letter_8" data-letters="8">8</div>
                    <div id="letter_9" data-letters="9">9</div>
                    <div id="letter_0" data-letters="0">0</div>
                    <div id="letter_minus" data-letters="-">-</div>
                    <div id="letter_plus" data-letters="+">+</div>
                    <div className="backspace" id="letter_backspace" data-spec="backspace">Backspace</div>
                </div>
                <div className="line">
                    <div className="tab" id="letter_tab" data-spec="tab">Tab</div>
                    <div id="letter_й" data-letters="Йй">Й</div>
                    <div id="letter_ц" data-letters="Цц">Ц</div>
                    <div id="letter_у" data-letters="Уу">У</div>
                    <div id="letter_к" data-letters="Кк">К</div>
                    <div id="letter_е" data-letters="Ее">Е</div>
                    <div id="letter_н" data-letters="Нн">Н</div>
                    <div id="letter_г" data-letters="Гг">Г</div>
                    <div id="letter_ш" data-letters="Шш">Ш</div>
                    <div id="letter_щ" data-letters="Щщ">Щ</div>
                    <div id="letter_з" data-letters="Зз">З</div>
                    <div id="letter_х" data-letters="Хх">Х</div>
                    <div id="letter_ъ" data-letters="Ъъ">Ъ</div>
                    <div id="letter_slash" data-letters="slash">/</div>
                </div>
                <div className="line">
                    <div className="caps" id="letter_capslock" data-spec="capslock">Caps Lock</div>
                    <div id="letter_ф" data-letters="Фф">Ф</div>
                    <div id="letter_ы" data-letters="Ыы">Ы</div>
                    <div id="letter_в" data-letters="Вв">В</div>
                    <div id="letter_а" data-letters="Аа">А</div>
                    <div id="letter_п" data-letters="Пп">П</div>
                    <div id="letter_р" data-letters="Рр">Р</div>
                    <div id="letter_о" data-letters="Оо">О</div>
                    <div id="letter_л" data-letters="Лл">Л</div>
                    <div id="letter_д" data-letters="Дд">Д</div>
                    <div id="letter_ж" data-letters="Жж">Ж</div>
                    <div id="letter_э" data-letters="Ээ">Э</div>
                    <div className="enter" id="letter_enter" data-spec="enter">Enter</div>
                </div>
                <div className="line">
                    <div className="shift" data-spec="shift">Shift</div>
                    <div id="letter_я" data-letters="Яя">Я</div>
                    <div id="letter_ч" data-letters="Чч">Ч</div>
                    <div id="letter_с" data-letters="Сс">С</div>
                    <div id="letter_м" data-letters="Мм">М</div>
                    <div id="letter_и" data-letters="Ии">И</div>
                    <div id="letter_т" data-letters="Тт">Т</div>
                    <div id="letter_ь" data-letters="Ьь">Ь</div>
                    <div id="letter_б" data-letters="Бб">Б</div>
                    <div id="letter_ю" data-letters="Юю">Ю</div>
                    <div id="letter_dot" data-spec="dot">. <sup>,</sup></div>
                    <div className="shift" data-spec="shift">Shift</div>
                </div>
                <div className="line">
                    <div className="space" id="letter_space" data-spec="space"></div>
                    <div className="alt" id="letter_alt" data-spec="alt">Alt</div>
                </div>
            </div>
        </div>
    )
};

export default Keyboard;
