import React, { Component } from 'react';
import { render } from 'react-dom';
import axios from 'axios';


    class extKeybComponent extends Component {
        state = {
            type: 'all-meat',
            paras: 5,
            format: 'json',
            text: []
        };

        handleSubmit = (e) => {
            e.preventDefault();
            let url = `https://baconipsum.com/api/?type=${this.state.type}&paras=${this.state.paras}&format=${this.state.format}`;
            axios.get(url)
                .then(res => {
                    if (this.state.format !== 'json') {
                        this.setState(prevState => {
                            return {
                                text: prevState.text.concat(res.data)
                            }
                        })
                    } else {
                        for (let key in res.data) {
                            this.setState(prevState => {
                                return {
                                    text: prevState.text.concat(res.data[key])
                                }
                            });
                        }
                    }
                })
                .catch(err => console.log(err));
        }}
